import React from 'react';
import '../App.css';
import Divider from '@material-ui/core/Divider';
import {BrowserRouter as Router, Link, Route} from 'react-router-dom'
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableHead from '@material-ui/core/TableHead';
import TableRow from '@material-ui/core/TableRow';

export default class DungeonInfo extends React.Component {

  constructor(props) {
    super(props);

    const pathname = window.location.pathname;
    const paths = pathname.split('/');
    const dungeonId = paths[2];

    this.titleize = this.titleize.bind(this);

    const dungeonName = this.titleize(dungeonId.replace('-', ' '));
    this.state = {
      dungeonName: dungeonName
    }

  }

  // temporary hack
  titleize(sentence) {
    if(!sentence.split) return sentence;
    var _titleizeWord = function(string) {
            return string.charAt(0).toUpperCase() + string.slice(1).toLowerCase();
        },
        result = [];
    sentence.split(" ").forEach(function(w) {
        result.push(_titleizeWord(w));
    });
    return result.join(" ");
  }


  render() {
    return(
      <div className="body-dungeon">
        <div className="title">
          {this.state.dungeonName}
        </div>
        <Divider style={{"background-color":"#8a8a8a"}} />
        <div className="sub-info">
          <h4> General Info </h4>
          Parent Dungeon: Ultimate Arena
          <br/>
          Stamina: 99
          <br/>
          Floors: 21
          <h4> Dungeon Mechanics </h4>
          Poison Skyfall, Damage Absorb, etc.
        </div>
        <div className="floors">
          <div className="floor">
            <h4> Floor 1 </h4>
            4 appear
            <Table>
              <TableHead>
                <TableRow>
                  <TableCell>Enemy</TableCell>
                  <TableCell>Typing</TableCell>
                  <TableCell>Turn Timer</TableCell>
                  <TableCell>Attack</TableCell>
                  <TableCell>Defense</TableCell>
                  <TableCell>HP</TableCell>
                </TableRow>
              </TableHead>
              <TableBody>
                <TableRow>
                  <TableCell>Red Evolution Mask</TableCell>
                  <TableCell>Evo Material</TableCell>
                  <TableCell>1</TableCell>
                  <TableCell>22000</TableCell>
                  <TableCell>500000000</TableCell>
                  <TableCell>6</TableCell>
                </TableRow>
              </TableBody>
            </Table>
          </div>
          <Divider style={{"background-color":"#8a8a8a"}} />
          <div className="floor">
            <h4> Floor 2 </h4>
            4 appear
            <Table>
              <TableHead>
                <TableRow style={{"border":"1px solid #222222"}}>
                  <TableCell>Enemy</TableCell>
                  <TableCell>Typing</TableCell>
                  <TableCell>Turn Timer</TableCell>
                  <TableCell>Attack</TableCell>
                  <TableCell>Defense</TableCell>
                  <TableCell>HP</TableCell>
                </TableRow>
              </TableHead>
              <TableBody>
                <TableRow>
                  <TableCell>Blue Evolution Mask</TableCell>
                  <TableCell>Evo Material</TableCell>
                  <TableCell>1</TableCell>
                  <TableCell>22000</TableCell>
                  <TableCell>500000000</TableCell>
                  <TableCell>6</TableCell>
                </TableRow>
              </TableBody>
            </Table>
          </div>
        </div>
        
      </div>
    );
  }
  
}
