import React from 'react';
import '../App.css';
import Divider from '@material-ui/core/Divider';
import {BrowserRouter as Router, Link, Route} from 'react-router-dom'
import Table from '@material-ui/core/Table';
import TableRow from '@material-ui/core/TableRow';
import TableHeaderColumn from '@material-ui/core/TableHeaderColumn';

export default class DungeonInfo extends React.Component {

  constructor(props) {
    super(props);

    const pathname = window.location.pathname;
    const paths = pathname.split('/');
    const dungeonId = paths[2];

    this.titleize = this.titleize.bind(this);

    const dungeonName = this.titleize(dungeonId.replace('-', ' '));
    this.state = {
      dungeonName: dungeonName
    }

  }

  // temporary hack
  titleize(sentence) {
    if(!sentence.split) return sentence;
    var _titleizeWord = function(string) {
            return string.charAt(0).toUpperCase() + string.slice(1).toLowerCase();
        },
        result = [];
    sentence.split(" ").forEach(function(w) {
        result.push(_titleizeWord(w));
    });
    return result.join(" ");
  }


  render() {
    return(
      <div className="body-dungeon">
        <div className="title">
          {this.state.dungeonName}
        </div>
        <Divider style={{"background-color":"#8a8a8a"}} />
        <div className="sub-info">
          <h4> General Info </h4>
          Parent Dungeon: Ultimate Arena
          <br/>
          Stamina: 99
          <br/>
          Floors: 21
          <h4> Dungeon Mechanics </h4>
          Poison Skyfall, Damage Absorb, etc.
        </div>
        <div className="floors">
          <h4> Floor 1 </h4>
          4 appear
          <Table>
            <TableRow>
              <TableHeaderColumn>Enemy</TableHeaderColumn>
              <TableHeaderColumn>Typing</TableHeaderColumn>
              <TableHeaderColumn>Turn Timer</TableHeaderColumn>
              <TableHeaderColumn>Attack</TableHeaderColumn>
              <TableHeaderColumn>Defense</TableHeaderColumn>
              <TableHeaderColumn>HP</TableHeaderColumn>
            </TableRow>

          </Table>
        </div>
      </div>
    );
  }
  
}
